// Import the axios library for making HTTP requests
import axios from 'axios';

// Define the API endpoint for Nova Poshta
const API_ENDPOINT = 'https://api.novaposhta.ua/v2.0/json/';

// Define a function for calculating shipping rates
function calculateShippingRate(weight, destination) {
    // Make a request to the Nova Poshta API to retrieve shipping rates
    axios.post(API_ENDPOINT, {
        modelName: 'InternetDocument',
        calledMethod: 'getDocumentPrice',
        methodProperties: {
            Weight: weight,
            ServiceType: 'WarehouseWarehouse',
            CitySender: 'city_ref',
            CityRecipient: destination,
            Cost: '0',
            CargoType: 'Cargo',
            SeatsAmount: '1'
        }
    })
    .then(response => {
        // Extract the shipping rate from the response
        const shippingRate = response.data.data[0].Cost;
        
        // Display the shipping rate to the user
        displayShippingRate(shippingRate);
    })
    .catch(error => {
        // Handle any errors that occur during the request
        console.error(error);
    });
}

